import React from 'react';
import {
    View,
    Text,
    Button,
    StyleSheet,
    ScrollView,
    Image,
    TouchableOpacity
} from "react-native";
import branch1 from '../assets/image/branch1.png';
import bubble from '../assets/icons/bubble.png';
import Icon from 'react-native-vector-icons/MaterialIcons';

const Shop1 = ({navigation}) => {
    return (
        <ScrollView style={{backgroundColor: '#01BCE4'}}>
            <View>
                <View style={{ height: 15, backgroundColor: '#01BCE4'}}>
                </View>
                <View style={styles.imgShape}>
                        <Image style={styles.img} source={branch1}/>
                    </View>
                <View style={{ height: 15, backgroundColor: '#01BCE4'}}>
                </View>
                <View style={styles.mainTextContainer}>
                    <View style={styles.shapeTextContainer}>
                        <Text style={{
                            color: 'black',
                            fontSize: 50,
                            fontWeight: '800',
                            }}>
                                Laundry Shop 1
                        </Text>
                        <View style={styles.description}>
                            <View style={{
                                height:80,
                                width: 20,
                                backgroundColor: '#01BCE4',
                                borderRadius:20,
                            }}>
                            </View>
                            <Text style={{left: 10, bottom:10 }}>
                            Lorem ipsum dolor sit amet, consectetur adipi scing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Mus mauris vitae ultricies leo integer. Fusce ut placerat orci nulla
                            </Text>
                        </View>

                        <View style={styles.scheduleContainer}>
                            <Icon name='schedule' color={'black'} size={50} />
                            <Text>

                            </Text>

                        </View>
                        
                        
                    </View>
                    {/* For button 'book' */}
                    <TouchableOpacity>
                        <View>

                        </View>
                    </TouchableOpacity>
                </View>

                <View style={[{ 
                    width: "100%",
                     }]}>
                
                    <Button
                        color="blue"
                        title="Book"
                        onPress={() => navigation.navigate('Shop1Menu')}
                    />
                </View>
               
            </View>
        </ScrollView>
    )
}
const styles = StyleSheet.create({
    imgShape: {
        backgroundColor:'white',
        borderRadius:20,
        alignContent: 'center',
        alignSelf: 'center', 
        paddingTop:30,
        paddingHorizontal: 20,
        width: '95%',
        height: 350,
    },
    img: {
        height:300,
        width: 350,
    },
    mainTextContainer: {
        backgroundColor:'white',
        borderRadius:20,
        alignContent: 'center',
        alignSelf: 'center', 
        paddingTop:30,
       // paddingHorizontal: 20,
        width: '95%',
        height: 400,
    },
    shapeTextContainer: {
        alignContent: 'center',
        paddingHorizontal: 20,
    },
    description: {
        flexDirection: 'row',
        alignItems: 'center',
        alignContent: 'center',
        justifyContent:'center',
        paddingHorizontal: 15,
    },
    scheduleContainer: {
        backgroundColor:'#F6F6F6',
        paddingTop: 20,
        borderRadius: 20,
        
        width: '90%',
        height: 300,

    },
    

})
export default Shop1;
