import react from "react";
import {
    View,
    Image
} from 'react-native';
import { createBottomTabNavigator, BottomTabBar } from "@react-navigation/bottom-tabs";

import Home from "../Screens/Home";
import { COLOR, icons } from "../assets/constants";
import bubble from "../assets/icons/bubble.png"

const Tab = createBottomTabNavigator();

const Tabs = () => {
    return (
        <Tab.Navigator>
            tabBarOption={{
                showLabel: false
            }}
            <Tab.Screen
                name="Home"
                component={Home}
                options={{
                    tabBarIcon: ({focused}) => {
                        <Image
                            source={bubble}
                            resizeMode="contain"
                            style={{
                               width: 100,
                               height:100,
                               tintColor: focused ? COLOR.primary : COLOR.secondary

                            }}

                        />
                    }
                }}
            /> 
        </Tab.Navigator>
    )
}
export default Tabs;
