export const COLOR ={
    //base colors
    primary: "#03b1fc", //twitter blue
    secondary:"#0080FF", //azure

    //colors
    black:"#1e1f20",
    white:"#ffffff",


    lightGray:"#f5f5f6",
    lightGray2:"#f6f6f7",
    lightGray3:"#efeff1",
    transparent:"transparent",
    darkgray:'#898C95',
};

export const SIZES ={
    //global sizes
    base: 8,
    font: 14,
    radius: 30,
    padding: 10,
    padding2: 12,

    //font sizes
    largeTitle: 50,
    h1: 30,
    h2: 22,
    h3: 20,
    h4: 18,
    body1: 30,
    body2: 20, 
    body3: 16,
    body4: 14,
    body5: 12,

    // app dimention
};
const appTheme ={COLORS, SIZES};
export default appTheme;
