import react from "react";
import {
    View,
    Image
} from 'react-native';
import { createBottomTabNavigator, BottomTabBar } from "@react-navigation/bottom-tabs";

import Home from "../Screens/Home";
import { COLOR, icons } from "../assets/constants";


const Tab = createBottomTabNavigator();

const Tabs = () => {
    return (
        <Tab.Navigator>
            <Tab.Screen
                name="Home"
                component={Home}
                options={{
                    tabBarIcon: ({focused}) => {
                        <Image
                            source={icons.bubble}
                            resizeMethod="auto"
                            style={{
                                width: 100,
                                height:100,
                                tintColor: focused ? COLOR.primary : COLOR.secondary

                            }}

                        />
                    }
                }}
            /> 
        </Tab.Navigator>
    )
}
export default Tabs;
