const Tab = createBottomTabNavigator();
const Stack = createNativeStackNavigator();

const HomeStack = ({navigate}) => {
  return (
    <Stack.Navigator>
      <Stack.Screen
        name="Home"
        component={Home}
      />
      <Stack.Screen
        name="Shop1"
        component={Shop1}
       
      />
       <Stack.Screen
        name="Shop2"
        component={Shop2}
        
      />
       <Stack.Screen
        name="Profile"
        component={Profile}
        
      />
    </Stack.Navigator>
  );
};

const TabNavigator = () => {
  <Tab.Navigator
    screenOptions={{
      headerShow: false,
    }}
  >
    <Tab.Screen 
      name="Home2"
      component={HomeStack}
      options={({route}) => ({
          tabBarStyle: {
            display: getTabBarVisibility(route),
            backgroundColor: 'blue',
          },
          tabBarIcon: ({color, size}) => (
            <Icon name="home" color={color} size={size} />
          ),
        })}
    />
    <Tab.Screen 
      name="Chat"
      component={Chat}
      options={({route}) => ({
          tabBarStyle: {
            display: getTabBarVisibility(route),
            backgroundColor: 'blue',
          },
          tabBarIcon: ({color, size}) => (
            <Icon name="Chat" color={color} size={size} />
          ),
        })}
    />
    <Tab.Screen 
      name="List"
      component={List}
      options={({route}) => ({
          tabBarStyle: {
            display: getTabBarVisibility(route),
            backgroundColor: 'blue',
          },
          tabBarIcon: ({color, size}) => (
            <Icon name="reciept" color={color} size={size} />
          ),
        })}
    />

  </Tab.Navigator>
}

export default TabNavigator;
