import react from "react";
import {
    View,
    Image,
    TouchableOpacity
} from 'react-native';
import { createBottomTabNavigator, BottomTabBar} from "@react-navigation/bottom-tabs";
import Messages from "../Screens/Messages";
import Home from "../Screens/Home";
import List from "../Screens/List";
import { COLOR, icons } from "../assets/constants";
import bubble from "../assets/icons/bubble.png"


const Tab = createBottomTabNavigator();

const Tabs = () => {
    return (
        <Tab.Navigator
            tabBarOptions={{
                showLabel: false,
                style: {
                    position: 'absolute',
                    bottom: 25,
                    left:20,
                    right: 20,
                    elevation: 0,
                    backgroundColor: 'green',
                    borderRadius: 15,
                    height:90,

                }
            }}
        >
        
            <Tab.Screen
                name="messages"
                component={Messages}
                options={{
                    tabBarIcon: ({focused}) => {
                        <Image
                            source={bubble}
                            resizeMode="contain"
                            style={{
                               width: '100%',
                               height:'100%',
                               

                            }}

                        />
                    }
                }}
            /> 

            <Tab.Screen
                name="List"
                component={List}
                options={{
                    tabBarIcon: ({focused}) => {
                        <Image
                            source={bubble}
                            resizeMode="contain"
                            style={{
                               width: '100%',
                               height:'100%',
                               

                            }}

                        />
                    }
                }}
            />

           
        </Tab.Navigator>
    )
}
export default Tabs;
