import React from 'react';
import {
    View,
    Text,
    Button,
    StyleSheet,
    Image,
    ScrollView,
    TouchableOpacity
} from "react-native";
import bubble from "../assets/icons/bubble.png"
import Icon from 'react-native-vector-icons/MaterialIcons';

const Home = ({navigation}) => {
    return (
        <ScrollView style={styles.scrollcontainer}>
            <View style={styles.header}>
                    <Image source={bubble}
                        style={{
                            height: 50,
                            width: 50
                        }}
                    />
                    <Text style={{
                        color: 'black',
                        fontSize: 40,
                        }}>
                        Lalaba
                    </Text>
                    <Icon name='person' color={'white'} size={50} 
                        onPress={() => navigation.navigate('Profile')}
                    />
                
            </View>
            <View style={styles.titleContainer} >
                <Text style={{
                    color: 'black',
                    fontSize: 25,
                    }}>
                    Shops Available for you!
                </Text>

            </View>
            <View style={styles.container}>
                <View style={styles.body}>
                    <View style={styles.buttonContainer1}>
                    
                        <Button title="Laundry Shop1"
                                onPress={() => navigation.navigate('Shop1')}
                        />
                        <TouchableOpacity onPress={()=> navigation.navigate('Shop1')}>
                            <Image source={bubble}
                            style={{
                                height: 50,
                                width: 50,
                            }} 
                            />
                        </TouchableOpacity>
                    </View>
                    <View style={styles.buttonContainer2}>
                        <Button title="Laundry Shop 2"
                                onPress={() => navigation.navigate('Shop2')}
                        />

                        <TouchableOpacity onPress={()=> navigation.navigate('Shop2')}>
                            <Image source={bubble}
                            style={{
                                height: 50,
                                width: 50,
                                }} 
                            />    
                        </TouchableOpacity>
                        
                    </View>
                </View>    
                  
                       {/* <Button title="Shop1"
                            onPress={() => navigation.navigate('Shop1')}
                        /> */}
        </View>
        </ScrollView>
    )
}

const styles = StyleSheet.create({
    scrollcontainer: {
        backgroundColor : 'white',
    },
    header: {
        height: 50,
        width: '100%',
        backgroundColor: '#46829e',
        flex: -1,
        justifyContent: 'space-between',
        flexDirection: 'row',
    },
    container: {
        paddingTop:100,
        backgroundColor: 'white',
    },
    body: {
        justifyContent:'space-around',
        flexDirection: 'row',
        alignItems: 'center',
        alignContent:'center',
    },
    buttonContainer1: {
        height:250,
        width: 200,
        borderRadius:20,
        backgroundColor: '#F6F6F6',
        justifyContent:'center',
        flexDirection: 'column',
        alignItems: 'center',
        alignContent:'center',
    },
    buttonContainer2: {
        height:250,
        width: 200, 
        borderRadius:20,
        backgroundColor: '#F6F6F6',
        justifyContent:'center',
        flexDirection: 'column',
        alignItems: 'center',
        alignContent:'center',
    },
    profilePicture: {
        width:100,
        height:100
        
    }










})

export default Home;
